<script>

//Import components
import Characters from '@/Characters'
//Import components



//Sounds Game
var button = new Audio("./sounds/button.wav");
var call_war = new Audio("./sounds/call-war.wav");
var gol = new Audio("./sounds/game-over-lost.wav");
var wtg = new Audio("./sounds/winthisgame.ogg");
var BeastAttack = new Audio("./sounds/beast-attack.wav");
var burning = new Audio("./sounds/burning.wav");
var slash = new Audio("./sounds/slash.mp3");

button.buffered
call_war.buffered
gol.buffered
wtg.buffered
BeastAttack.buffered
slash.buffered
burning.buffered
BeastAttack.volume = 0.1;
//Sounds Game


export default {
  name: "App",
  data() {
    return {
      AttackBtnControl:true,
      publicPath: process.env.BASE_URL,
      logs: [{ log: "" }],
      logsB: [{ log: "" }],
      CharacterID: 0,
      P1W: 100,
      P2W: 100,
      SaldirPuan: 0,
      CanavarSaldirPuan: 0,
      Sonuc: "",
      ResultWin: false,
      ResultLose: false,
      ResultCss: "",
      Login: 0,
      Score1: 0,
      Score2: 0,
      Round: 1,
      ShowDmg1: false,
      ShowDmg2: false,
      RandomCss: "",
      GameOver: false,
      RandomTop: 0,
      RandomLeft: 0,
      CanavarSaldirOto: false,
      CanavarHPOto: false,
      CHPO_Interval: null,
      CSO_Interval: null,
      PlayerName: "Player",
      //HELL ATTACK TIMER
      timerEnabled: false,
      timerCount: 10,
      TimerDisabledBtn: 0,
      //SLASH ATTACK TIMER
      timerEnabledS: false,
      timerCountS: 5,
      TimerDisabledBtnS: 0,
      //HEALTH TAKE TIMER
      timerEnabledH: false,
      timerCountH: 1,
      TimerDisabledBtnH: 0,
      //ATTACK TAKE TIMER
      timerEnabledA: false,
      timerCountA: 1,
      TimerDisabledBtnA: 0,
      //BEAST ATTACK TIMER
      timerEnabledBA: true,
      timerCountBA: 5,
      TimerDisabledBtnBA: 5,
      // Sounds
    };
  },
  components: {
    Characters
  },
  methods: {
    NextPage: function (page) {
      button.play();
      if (page == 1) {
        button.play();
        return (this.Login = 1);
      } else if (page == 2) {
        button.play();
        return (this.Login = 2);
      } else if (page == 3) {
        button.play();
        call_war.currentTime = 0
        call_war.play(); // Begin game
        return (this.Login = 3);
      } else if (page == 4) {
        return (this.Login = 4);
      }
    },

    AttackKeyUp: function (e) {
      var keyCode = e.keyCode;
      if (keyCode == 49 && document.getElementById("Attack")) {
        document.getElementById("Attack").click();
      }
    },
    SlashKeyUp: function (e) {
      var keyCode = e.keyCode;
      if (keyCode == 51 && document.getElementById("SlashAttack")) {
        document.getElementById("SlashAttack").click();
      }
    },
    HPKeyUp: function (e) {
      var keyCode = e.keyCode;
      if (keyCode == 50 && document.getElementById("HP")) {
        document.getElementById("HP").click();
      }
    },
    HellKeyUp: function (e) {
      var keyCode = e.keyCode;
      if (keyCode == 52 && document.getElementById("HellAttack")) {
        document.getElementById("HellAttack").click();
      }
    },
    Attack: function () {
      document.getElementById("Attack").disabled = true;
      this.timerEnabledA = true;
      this.TimerDisabledBtnA = 1;
      this.SaldirPuan = Math.floor(Math.random() * 5) + 0;
      this.P1W = this.P1W - this.SaldirPuan;
      this.logsB.push({ log: this.SaldirPuan + " damage" });
      this.ShowDmg2 = true;
      this.ShowDmg = true;
      this.CanavarSaldirOto = true;
      this.CanavarHPOto = true;
        
      if (this.SaldirPuan == 0) {
        this.logsB.push({ log: "Miss" });
      }
    },
    hp: function () {
      document.getElementById("HP").disabled = true;
      this.timerEnabledH = true;
      this.SaldirPuan = 2;
      this.P2W = this.P2W + this.SaldirPuan;
      this.TimerDisabledBtnH = 1;
      this.logs.push({ log: "+ 5 life points" });
      3;
    },
    HellAttack: function () {
      document.getElementById("HellAttack").disabled = true;
      this.timerEnabled = true;
      this.SaldirPuan = Math.floor(Math.random() * 20) + 2;
      this.P1W = this.P1W - this.SaldirPuan;
      this.TimerDisabledBtn = 10;
      this.logsB.push({ log: this.SaldirPuan + " damage" });
      setTimeout(() => {}, 2000);
    },
    SlashAttack: function () {
      this.timerEnabledS = true;
      this.TimerDisabledBtnS = 5;
      document.getElementById("SlashAttack").disabled = true;
      this.SaldirPuan = Math.floor(Math.random() * 10) + 2;
      this.logsB.push({ log: this.SaldirPuan + " damage" });
      this.P1W = this.P1W - this.SaldirPuan;
    },
    RandomPosition () {
      this.RandomTop = Math.floor(Math.random() * (70 - 10)) + 10;
      this.RandomLeft = Math.floor(Math.random() * (80 - 0)) + 0;
    },
    ResultClose () {
      button.play();
      call_war.play();
      this.ResultWin = false;
      this.ResultLose = false;
      this.ResultCss = "";
      document.getElementById("round").style.transform = "scale(1)";
      document.getElementById("round").style.background = "transparent";
      this.AttackBtnControl=true;
    },
   GameOverTrue () {
      button.play();
      this.ResultWin = false;
      this.ResultLose = false;
      this.GameOver = false;
      this.ResultCss = "";
      this.Score1 = 0;
      this.Score2 = 0;
      this.Round = 1;
      this.P1W= 100;
      this.P2W= 100;
      this.SaldirPuan= 0;
      this.CanavarSaldirPuan= 0;
      this.ShowDmg1= false;
      this.ShowDmg2= false;
      document.getElementById("round").style.transform = "scale(1)";
      document.getElementById("round").style.background = "transparent";
      this.AttackBtnControl=true;
      this.Login = 1;
   }
  },
  mounted() {
    window.addEventListener("keyup", this.AttackKeyUp);
    window.addEventListener("keyup", this.HellKeyUp);
    window.addEventListener("keyup", this.HPKeyUp);
    window.addEventListener("keyup", this.SlashKeyUp);


    //var dark = new Audio ('./sounds/dark.wav');
    //  dark.play();
  },
  watch: {
    CanavarSaldirOto: function (value) {
      if (value == true) {
        this.CSO_Interval = setInterval(() => {
          this.ShowDmg1 = true;
          BeastAttack.play()
          this.RandomPosition();
          this.logs.push({ log: this.CanavarSaldirPuan + " - It hit you" });
          this.CanavarSaldirPuan = Math.floor(Math.random() * 10) + 0;
          if (this.P2W > 0) {
            this.P2W = this.P2W - this.CanavarSaldirPuan;
            document.getElementById("Sword_2").style.animationPlayState = "running";
            document.getElementById("Sword_2_10").style.animationPlayState = "running";
            document.getElementById("hearth_4").style.animationPlayState = "running";
          }
          if (this.CanavarSaldirPuan === 0) {
            this.logs.push({ log: "Miss" });
          }
        }, 1000);
      } else {
        clearInterval(this.CSO_Interval);
        document.getElementById("Sword_2").style.animationPlayState = "paused";
        document.getElementById("Sword_2_10").style.animationPlayState = "paused";
        document.getElementById("hearth_4").style.animationPlayState = "paused";
        this.ShowDmg2 = false;
        this.ShowDmg1 = false;
        this.CanavarSaldirOto = false;
      }
    },
    CanavarHPOto: function (value) {
      if (value == true && this.P1W <= 100) {
        this.CHPO_Interval = setInterval(() => {
          this.ShowDmg1 = true;
          this.RandomPosition();
          this.logsB.push({
            log: "+ " + this.CanavarSaldirPuan + " life points",
          });
          this.CanavarSaldirPuan = Math.floor(Math.random() * 5) + 1;
          if (this.P1W > 0) {
            this.P1W = this.P1W + this.CanavarSaldirPuan;
            document.getElementById("Sword_2").style.animationPlayState ="running";
            document.getElementById("Sword_2_10").style.animationPlayState = "running";
          }
        }, 2000);
      } else {
        clearInterval(this.CHPO_Interval);
        document.getElementById("Sword_2").style.animationPlayState = "paused";
        document.getElementById("Sword_2_10").style.animationPlayState ="paused";
        this.CanavarHPOto = false;
      }
    },

    //* Player main
    P2W: function (value) {
      if (value <= 0) {
        this.P2W = 0;
        this.AttackBtnControl=false;
        gol.play();
        this.timerEnabledA = false;
        this.ResultCss = "z-index: 10;background-color: #f44336; transform:scale(1)";
        this.Sonuc = this.Round + ".Round - You Lost";
        this.ResultLose = true;
        document.getElementById("round").style.transform = "scale(15)";
        document.getElementById("round").style.background = "black";
        this.CanavarSaldirOto = false;
        this.CanavarHPOto = false;
        this.SaldirPuan = 0;
        this.CanavarSaldirPuan = 0;
        this.P1W = 100;
        this.P2W = 100;
        this.logs = [];
        this.logsB = [];
        this.RandomCss = "";
        this.RandomTop = 0;
        this.RandomLeft = 0;
        this.Round++;
        this.Score2++;
        console.log("Kaybettin");
      }
      else if (value >= 100){
          this.P2W=100;
      }
    },

    //! Beast
    P1W: function (value) {
      if (value <= 0) {
        this.P1W=0;
        this.AttackBtnControl=false;
        wtg.play();
        this.timerEnabledA = false;
        this.ResultWin = true;
        this.ResultCss =
          "z-index: 10;background-color: #4caf50; transform:scale(1)";
        this.Sonuc = this.Round + ".Round - You Win";
        document.getElementById("round").style.transform = "scale(15)";
        document.getElementById("round").style.background = "black";
        this.CanavarSaldirOto = false;
        this.CanavarHPOto = false;
        this.SaldirPuan = 0;
        this.CanavarSaldirPuan = 0;
        this.P1W = 100;
        this.P2W = 100;
        this.logs = [];
        this.logsB = [];
        this.RandomCss = "";
        this.RandomTop = 0;
        this.RandomLeft = 0;
        this.Round++;
        this.Score1++;
        console.log("Kazandın");
      }
       else if (value >= 100){
          this.P1W=100;
      }
    },

 
    // !HellTiming
    timerEnabled(value) {
      if (value) {
        burning.play();
        setTimeout(() => {
          this.timerCount--;
        }, 1000);
      }
    },

    timerCount: {
      handler(value) {
        if (value > 0 && this.timerEnabled) {
          setTimeout(() => {
            this.timerCount--;
            this.TimerDisabledBtn -= 1;
          }, 1000);
        } else {
          this.timerCount = 10;
          this.timerEnabled = false;
          this.TimerDisabledBtn = 0;
          if (document.getElementById("HellAttack")) {
            document.getElementById("HellAttack").disabled = false;
          }
        }
      },
    },
    // !HellTiming
    // !SlashTiming
    timerEnabledS: function (value) {
      if (value) {
        slash.play();
        setTimeout(() => {
          this.timerCountS--;
        }, 1000);
      }
    },
    timerCountS: {
      handler(value) {
        if (value > 0 && this.timerEnabledS) {
          setTimeout(() => {
            this.timerCountS--;
            this.TimerDisabledBtnS -= 1;
          }, 1000);
        } else {
          this.timerCountS = 5;
          this.timerEnabledS = false;
          this.TimerDisabledBtnS = 0;
          if (document.getElementById("SlashAttack")) {
            document.getElementById("SlashAttack").disabled = false;
          }
        }
      },
    },
