<template>
  <div class="stars">
    <div v-for="index in 10" :key="index" class="star"></div>
  </div>
  <nav v-if="Login == 3" class="nav">
    <img class="logo" src="/img/logo.svg" alt="" />
    <div class="score">
      <Characters name="ScoreBar" />
      <span class="belirt">
        <span class="skor1">{{ Score1 }}</span>
        <span class="skor2">{{ Score2 }}</span>
      </span>
      <span id="round"
        >Round: <strong>{{ Round }}</strong></span
      >
    </div>
    <button style="margin-right:70px;" class="HowToPlay" @click="ShowInfo()">
      <span>{{ IconQuestion }}</span>
    </button>
    <div
      v-if="IconQuestion === 'x'"
      class="Result"
      style="z-index: 10;background-color:rgb(46 18 72 / 95%); transform:scale(1)"
    >
     <p>
        How to Play ?
      </p>
      <p>
        <Characters name="Keyboard" />
      </p>
    </div>
  </nav>
  <!--! Nickname Screen-->
  <div v-if="Login == 0" class="NicknameScreen">
    <div class="welcome">Welcome <br />{{ PlayerName }}</div>
    <input
      id="textbox"
      v-model="PlayerName"
      class="textbox"
      type="text"
      placeholder="Write Nickname"
    />
    <button v-show="PlayerName !== ''" id="Basla" @click="NextPage(1)">
      Next
    </button>
  </div>
  <!--! Nickname Screen-->

  <!--********************************************************************** Begin Choose your warrior screen-->
  <div v-if="Login == 1" class="ChooseScreen">
    <div class="welcome">Choose your character</div>
    <div class="CharactersFlx">
      <span class="Characters"
        ><Characters
          tabindex="1"
          @click="CharacterID = 1"
          name="Ch_A"
        /><!-- Component --></span
      >
      <span class="Characters"
        ><Characters
          tabindex="2"
          @click="CharacterID = 2"
          name="Ch_B"
        /><!-- Component --></span
      >
      <span class="Characters"
        ><Characters
          tabindex="3"
          @click="CharacterID = 5"
          name="Ch_E"
        /><!-- Component -->
      </span>
      <span class="Characters">
        <Characters
          tabindex="4"
          @click="CharacterID = 3"
          name="Ch_C"
        /><!-- Component --></span
      >
      <span class="Characters"
        ><Characters
          tabindex="5"
          @click="CharacterID = 4"
          name="Ch_D"
        /><!-- Component -->
      </span>
      <span class="Characters"
        ><Characters
          tabindex="6"
          @click="CharacterID = 6"
          name="Ch_F"
        /><!-- Component -->
      </span>
      <span class="Characters"
        ><Characters
          tabindex="7"
          @click="CharacterID = 7"
          name="Ch_G"
        /><!-- Component -->
      </span>
    </div>
    <button
      @keyup.enter="NextPage(3)"
      v-show="CharacterID > 0"
      id="Basla"
      @click="NextPage(3)"
    >
      Start
    </button>
  </div>
  <!--********************************************************************** Finish Choose your warrior screen-->
  <div class="Result" :style="ResultCss">
    <div class="win">
      <p>{{ Sonuc }}</p>
      <button v-if="ResultWin" class="PlayNextRound" @click="ResultClose">
        Play next round
      </button>
      <button
        v-else-if="ResultLose"
        class="PlayNextRoundLose"
        @click="ResultClose"
      >
        Play next round
      </button>
      <button v-else-if="GameOver" class="PlayAgain" @click="GameOverTrue">
        PlayAgain
      </button>
    </div>
  </div>

  <div v-if="Login == 3" class="players">
